package JavaExecute;

import JavaOOPs.AbstractRun;
import JavaOOPs.AggregationRun;
import JavaOOPs.EncapsulationRun;
import JavaOOPs.InheritanceRun;
import JavaOOPs.PolymorphismRun;

public class ExecuteOOPs {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("OOPs execution started >>>");
		
		EncapsulationRun.Encap();
		InheritanceRun.Inherit();
		PolymorphismRun.polymor();
		AbstractRun.abstracte();
		AggregationRun.Aggregate();

	}

}
